document.addEventListener("DOMContentLoaded", function () {
  fetch("./data/BScStudents.json")
    .then((a) => a.json())
    .then((a) => {
      ourTeam = a.BScStudents;
      let e = document.getElementById("ourteam_box_team");
      (ourTeamResult = ""),
        ourTeam.map((a, e) => {
          let t =
            "" == a.image
              ? `<div class=${
                  e % 2 == 0 ? "empty_image_box1" : "empty_image_box2"
                }></div>`
              : ` <img alt="profile user" data-src=${a.image} class="lazyload profile_img_team" />`;
          ourTeamResult += `<div class="card">
              <div class="image_box">
               ${t}
              </div>
              <div class="text_team">
                <p class="name_text_team">${a.name}</p>
                <p class="status_text_team">${a.field}</p>
                <div class="icons_team">
                  ${
                    a.linkedin
                      ? `<a aria-label="linkedin" href="${a.linkedin}" class="tooltip" target="_blank"><img alt="icon linkedin"  src="./image/linkedin.png" class="icons_team_img" />
                      <span class="tooltiptext">${a.name} 's LinkedIn</span></a>`
                      : ""
                  }
                  ${
                    a.github
                      ? `<a aria-label="github" href="${a.github}" class="tooltip" target="_blank">
                      <span class="tooltiptext">${a.name} 's GitHub</span><img alt="icon github"  src="./image/git.png" class="icons_team_img" /></a>`
                      : ""
                  }
                  ${
                    a.google_scholar
                      ? `<a aria-label="google_scholar" href="${a.google_scholar}" class="tooltip" target="_blank"><img alt="icon google_scholar"  src="./image/google.png" class="icons_team_img" />
                      <span class="tooltiptext">${a.name} 's Google Scholar</span></a>`
                      : ""
                  }
                  ${
                    a.homepage
                      ? `<a aria-label="website" href="${a.homepage}" class="tooltip" target="_blank"><img  alt="icon website"  src="./image/website.png" class="icons_team_img" />
                      <span class="tooltiptext">${a.name} 's Website</span></a>`
                      : ""
                  }
                </div> 
              </div>
            </div>  
      `;
        }),
        (e.innerHTML = ourTeamResult);
    })
    .catch((a) => console.error("Error fetching data:", a));
});
