document.addEventListener("DOMContentLoaded", function () {
  fetch("./data/PhDStudents.json")
    .then((a) => a.json())
    .then((a) => {
      PHDStudents = a.PhDStudents;
      let e = document.getElementById("PHDStudents_box_team");
      (PHDStudentsResult = ""),
        PHDStudents.map((a, e) => {
          PHDStudentsResult += `<div class="card">
              <div class="image_box">
               ${
                 "" == a.image
                   ? `<div class=${
                       e % 2 == 0 ? "empty_image_box1" : "empty_image_box2"
                     }></div>`
                   : ` <img alt="image profile user" class="lazyload profile_img_team" data-src=${a.image}   />`
               }
              </div>
              <div class="text_team">
                <p class="name_text_team">${a.name}</p>
                <p class="status_text_team">${a.field}</p>
                <div class="icons_team">
                  ${
                    a.linkedin
                      ? `<a aria-label="linkedin" href="${a.linkedin}" class="tooltip" target="_blank"><img alt="icon small linkedin" src="./image/linkedin.png" class="icons_team_img" />
                      <span class="tooltiptext">${a.name} 's LinkedIn</span></a>`
                      : ""
                  }
                  ${
                    a.github
                      ? `<a aria-label="github" href="${a.github}" class="tooltip" target="_blank"><img alt="icon small github" src="./image/git.png" class="icons_team_img" />
                      <span class="tooltiptext">${a.name} 's GitHub</span></a>`
                      : ""
                  }
                  ${
                    a.google_scholar
                      ? `<a aria-label="google-scholar" href="${a.google_scholar}" class="tooltip" target="_blank"><img alt="icon small google-scholar" src="./image/google.png" class="icons_team_img" />
                      <span class="tooltiptext">${a.name} 's Google Scholar</span></a>`
                      : ""
                  }
                  ${
                    a.homepage
                      ? `<a aria-label="homepage" href="${a.homepage}" class="tooltip" target="_blank"><img alt="icon small website" src="./image/website.png" class="icons_team_img" />
                      <span class="tooltiptext">${a.name} 's Website</span></a>`
                      : ""
                  }
                </div> 
              </div>
            </div>  
      `;
        }),
        (e.innerHTML = PHDStudentsResult);
    })
    .catch((a) => console.error("Error fetching data:", a));
});
