document.addEventListener("DOMContentLoaded",function(){fetch("./data/alumni.json").then(a=>a.json()).then(a=>{alumni=a.alumni;let e=document.getElementById("alumni_box_team");result="",alumni.map(a=>result+=`
        <div class="card1">
              <div class="text_team1">
                <p class="name_text_team">${a.name}</p>
                ${a.current_position !== "" ?
                    `<p class="status_text_team">Former ${a.degree} (${a.entry_year}-${a.graduation_year}), now ${a.current_position} at ${a.current_company}, ${a.country}</p>` :
                    `<p class="status_text_team">Former ${a.degree} (${a.entry_year}-${a.graduation_year})</p>`
                }
                <div class="icons_team1">
                  ${a.linkedin?`<a aria-label="linkedin" href="${a.linkedin}"  target="_blank"><img alt="icon linkedin" src="./image/linkedin.png" class="icons_team_img" />
                      
                      </a>`:""}
                  ${a.github?`<a aria-label="github" href="${a.github}"  target="_blank"><img alt="icon github" src="./image/git.png" class="icons_team_img" />
                     
                      </a>`:""}
                  ${a.google_scholar?`<a aria-label="google_scholar" href="${a.google_scholar}"  target="_blank"><img alt="icon google_scholar" src="./image/google.png" class="icons_team_img" />
                     
                      </a>`:""}
                  ${a.researchgate?`<a aria-label="researchgate" href="${a.researchgate}"  target="_blank"><img alt="icon researchgate" src="./image/rg.png" class="icons_team_img" />
                     
                      </a>`:""}
                  ${a.homepage?`<a aria-label="website" href="${a.homepage}"  target="_blank"><img alt="icon website" src="./image/website.png" class="icons_team_img" />
                      
                      </a>`:""}
                </div> 
              </div>
            </div> 
      `),e.innerHTML=result}).catch(a=>console.error("Error fetching data:",a))});
