document.addEventListener("DOMContentLoaded", function () {
  fetch("./data/alumni.json")
    .then((a) => a.json())
    .then((a) => {
      alumni = a.alumni;
      let e = document.getElementById("alumni_box_team");
      (result = ""),
        alumni.map(
          (a) =>
            (result += `
        <div class="card1">
              <div class="text_team1">
                <p class="name_text_team">${a.name}</p>
                <p class="status_text_team">${a.field}</p>
                <div class="icons_team1">
                  ${
                    a.linkedin
                      ? `<a aria-label="linkedin" href="${a.linkedin}" class="tooltip" target="_blank"><img alt="icon linkedin" src="./image/linkedin.png" class="icons_team_img" />
                      <span class="tooltiptext">${a.name} 's LinkedIn</span>
                      </a>`
                      : ""
                  }
                  ${
                    a.github
                      ? `<a aria-label="github" href="${a.github}" class="tooltip" target="_blank"><img alt="icon github" src="./image/git.png" class="icons_team_img" />
                      <span class="tooltiptext">${a.name} 's GitHub</span>
                      </a>`
                      : ""
                  }
                  ${
                    a.google_scholar
                      ? `<a aria-label="google_scholar" href="${a.google_scholar}" class="tooltip" target="_blank"><img alt="icon google_scholar" src="./image/google.png" class="icons_team_img" />
                      <span class="tooltiptext">${a.name} 's Google Scholar</span>
                      </a>`
                      : ""
                  }
                  ${
                    a.homepage
                      ? `<a aria-label="website" href="${a.homepage}" class="tooltip" target="_blank"><img alt="icon website" src="./image/website.png" class="icons_team_img" />
                      <span class="tooltiptext">${a.name} 's Website</span>
                      </a>`
                      : ""
                  }
                </div> 
              </div>
            </div> 
      `)
        ),
        (e.innerHTML = result);
    })
    .catch((a) => console.error("Error fetching data:", a));
});
