function debounce(e, t) {
  let r;
  return function () {
    let i = this,
      c = arguments;
    clearTimeout(r),
      (r = setTimeout(() => {
        e.apply(i, c);
      }, t));
  };
}
function setActiveLink() {
  let e = document.querySelectorAll(".link_header"),
    t = document.querySelectorAll("section"),
    r = document.querySelector("header");
  document.querySelector(".main").scrollTop > 0
    ? r.classList.add("scrolled")
    : r.classList.remove("scrolled"),
    t.forEach(function (t) {
      let r = t.getBoundingClientRect();
      if (r.top <= 100 && r.bottom >= 100) {
        let i = t.getAttribute("id");
        e.forEach(function (e) {
          e.getAttribute("aria-label").toLowerCase().includes(i)
            ? e.classList.add("active")
            : e.classList.remove("active");
        });
      }
    });
}
document.addEventListener("DOMContentLoaded", setActiveLink()),
  document
    .querySelector(".main")
    .addEventListener("scroll", debounce(setActiveLink, 10));
const hamburger = document.querySelector(".hamburger"),
  headerList = document.querySelector(".header_list");
hamburger.addEventListener("click", () => {
  hamburger.classList.toggle("active"), headerList.classList.toggle("active");
});
function smoothScroll(event) {
  //   event.preventDefault(); // Prevent default anchor behavior
  const targetSection = document.getElementById(event); // Find the target section in the document
  const main = document.querySelector(".main"); // Find the target section in the document

  if (targetSection) {
    main.scrollTo({
      top: targetSection.offsetTop,
      behavior: "smooth",
    });
    // targetSection.scrollIntoView({
    //   behavior: "smooth",
    // });
  }
}
async function copyContent() {
  try {
    text = document.querySelector(".value_modal").textContent;
    console.log(text);
    const tooltip = document.getElementById("tooltip");
    tooltip.classList.add("activeCopy");

    setTimeout(function () {
      tooltip.classList.remove("activeCopy");
    }, 2000);
    await navigator.clipboard.writeText(text);
    console.log("Content copied to clipboard");
  } catch (err) {
    console.error("Failed to copy: ", err);
  }
}
