function debounce(e, t) {
  let l;
  return function () {
    let c = this,
      i = arguments;
    clearTimeout(l),
      (l = setTimeout(() => {
        e.apply(c, i);
      }, t));
  };
}
function setActiveLink() {
  let e = document.querySelectorAll(".link_header"),
    t = document.querySelectorAll("section"),
    l = document.querySelector("header");
  window.scrollY > 0
    ? l.classList.add("scrolled")
    : l.classList.remove("scrolled"),
    t.forEach(function (t) {
      let l = t.getBoundingClientRect();
      if (l.top <= 100 && l.bottom >= 100) {
        let c = t.getAttribute("id");
        e.forEach(function (e) {
          e.getAttribute("href") === "#" + c
            ? e.classList.add("active")
            : e.classList.remove("active");
        });
      }
    });
}
document.addEventListener("DOMContentLoaded", setActiveLink),
  window.addEventListener("scroll", debounce(setActiveLink, 10)),
  hamburger.addEventListener("click", () => {
    let e = document.querySelector(".hamburger"),
      t = document.querySelector(".header_list");
    e.classList.toggle("active"), t.classList.toggle("active");
  });
