function debounce(e,t){let r;return function(){let i=this,c=arguments;clearTimeout(r),r=setTimeout(()=>{e.apply(i,c)},t)}}function setActiveLink(){let e=document.querySelectorAll(".link_header"),t=document.querySelectorAll("section"),r=document.querySelector("header");window.scrollY>0?r.classList.add("scrolled"):r.classList.remove("scrolled"),t.forEach(function(t){let r=t.getBoundingClientRect();if(r.top<=100&&r.bottom>=100){let i=t.getAttribute("id");e.forEach(function(e){e.getAttribute("href")==="#"+i?e.classList.add("active"):e.classList.remove("active")})}})}document.addEventListener("DOMContentLoaded",setActiveLink),window.addEventListener("scroll",debounce(setActiveLink,10));const hamburger=document.querySelector(".hamburger"),headerList=document.querySelector(".header_list");hamburger.addEventListener("click",()=>{hamburger.classList.toggle("active"),headerList.classList.toggle("active")});
